function spec(b) {
  var server = b.server || require('./server');
  var config = require('./config');
  var BracketBuilder = b.BracketBuilder || require('./model/bracketBuilder').class();

  var Router = function() {
    var bracketBuilder = new BracketBuilder();

    server.post('/brackets/create', bracketBuilder.build.bind(bracketBuilder));
  };

  return Router;
}

module.defineClass(spec);